import React from 'react';
import { graphql, compose } from 'react-apollo';
import { getSingers } from '../queries/queries';
const AddSong = props => {
	function displaySingers() {
		const data = props.data;
		console.log(data.singers)
		if(data.loading)
			return <option>Loading...</option>
		else return data.singers.map(singer => <option key={singer.name}>{singer.name}</option>)
	}
	function handleSubmit(e) {
		e.preventDefault();
	}
	return(
		<form onSubmit={handleSubmit()}>
			<label>
				Tên bài hát:
				<input type="text" required/>
			</label><br/>
			<label>
				Thể loại:
				<input type="text" required/>
			</label><br/>
			<label>
				Ca sĩ
				<select>
					<option>Chọn ca sĩ</option>
					{displaySingers()}
				</select>
			</label>
			<button>+</button>
		</form>
	)
}

export default graphql(getSingers)(AddSong)
