import React from 'react';
import { graphql } from 'react-apollo';
import { getSong } from '../queries/queries'
const Details = props => {
	const {book} = props.data;
	if(book) 
	return(
		<div>
			<p>{book.name}</p>
		</div>
	)
}

export default graphql(getSong, {
	options: (props) => {
		return {
			variables: {
				id: props.id
			}
		}
	}
}
)(Details)
