import React from 'react'
import { getSongs, getAll } from '../queries/queries';
import { graphql, compose } from 'react-apollo';
import AddSong from './AddSong'


const Songs = props => {
	function displaySongs() {
		const data = props.getSongs;
		if(data.loading)
			return <div>Loading....</div>
		else
			return data.songs.map(song => <li className="w3-bar-item w3-btn w3-round w3-padding w3-card-4 w3-margin w3-white" key={song.id}>{song.name}</li>)
	};

	function details() {
