import React from 'react';
import { graphql } from 'react-apollo';
import { getSong } from '../queries/queries'
const Details = props => {
	function displayBook() {
		const {song} = props.data;
		if(song) {
			return(
				<div>
					<p>Tên bài hát: {song.name}</p>
					<p>Thể loại{song.genre}</p>
					<p>Tên ca sĩ: {song.singer.name}</p>
					<p>Tuổi: {song.singer.age}</p>
					<p>Bài hát: </p>
					<ul>
						{song.singer.songs.map(song => <li key={song.id}>Tên: {song.name}</li>)}
					</ul>
				</div>
			)
		} 
	}
	return(
		<div>
			{displayBook()}
		</div>
	)
}

export default graphql(getSong, {
	options: (props) => {
		return {
			variables: {
				id: props.id
			}
		}
	}
}
)(Details)
