import React, {useState} from 'react';
import { graphql, compose } from 'react-apollo';
import { getSingers } from '../queries/queries';
const AddSong = props => {
	function displaySingers() {
		const [name, setName] = useState('');
		const [genre, setGenre] = useState('');
		const [singerid, setSingerid] = useState('');

		const data = props.data;
		console.log(data.singers)
		if(data.loading)
			return <option>Loading...</option>
		else return data.singers.map(singer => <option key={singer.id} value={singer.id}>{singer.name}</option>)
	}
	function handleSubmit(e) {
		e.preventDefault();
	}
	return(
		<form onSubmit={e => handleSubmit(e)}>
			<label>
				Tên bài hát:
				<input type="text" required onChange={e => setName(e.target.value)}/>
			</label><br/>
			<label>
				Thể loại:
				<input type="text" required onChange={e => setGenre(e.target.value)}/>
			</label><br/>
			<label>
				Ca sĩ
				<select onChange={e => setName(e.target.value)}>
					<option>Chọn ca sĩ</option>
					{displaySingers()}
				</select>
			</label>
			<button>+</button>
		</form>
	)
}

export default graphql(getSingers)(AddSong)
