import {gql} from 'apollo-boost';

const getSongs = gql`
	{
		songs {
			id, 
			name	
		}
	}
`
const getSingers = gql`
	{
		singers {
			name
		}
	}
`

const addSong = gql`
	mutation {
		addSong($name: String!, $genre: String!, $authorid: ID!) {
			name: $name,
			genre: $genre,
			authorid: $authorid
		}
	}
`
export { getSongs, getSingers }
