import React from 'react'
import { getSongs } from '../queries/queries';
import { graphql } from 'react-apollo';
import AddSong from './AddSong'


const Songs = props => {
	function displaySongs() {
		const data = props.data;
		if(data.loading)
			return <div>Loading....</div>
		else
			return data.songs.map(song => <li className="w3-bar-item w3-btn w3-round w3-padding w3-card-4" key={song.id}>{song.name}</li>)
	}
	return(
		<div>
			<h2>Song List</h2>
			<ul className="w3-bar">
				{displaySongs()}
			</ul>
			<AddSong/>
		</div>
	)
}

export default graphql(getSongs)(Songs)
