const AddSinger = props => {
	return(
		<form id="addsinger" className="w3-padding w3-round w3-border w3-border-white w3-margin">
			<label>
				Tên ca sĩ: {' '}
				<input type='text'/>
			</label><br/>
			<label>
				Tên bài hát: {' '}
				<input type='text'/>
			</label>
			<button>+</button>
		</form>
	)
}

export default graphql(addingSinger)(AddSinger)
