import React from 'react';
import { graphql, compose } from 'react-apollo';
import { getSingers } from '../queries/queries';
const AddSong = props => {
	function displaySingers() {
		const data = props.data;
		if(data.loading)
			return <option>Loading...</option>
		else data.singers.map(singer => <option key={singer.id}>{singer.name}</option>)
	}
	return(
		<form>
			<label>
				Tên bài hát:
				<input type="text"/>
			</label><br/>
			<label>
				Thể loại:
				<input type="text"/>
			</label><br/>
			<label>
				Ca sĩ
				<select>
					<option>Chọn ca sĩ</option>
					{displaySingers()}
				</select>
			</label>
		</form>
	)
}

export default graphql(getSingers)(AddSong)
