import {gql} from 'apollo-boost';

const getSongs = gql`
	{
		songs {
			id, 
			name	
		}
	}
`
const getSingers = gql`
	{
		singers {
			name,
			id
		}
	}
`

const addSong = gql`
	mutation ($name: String!, $genre: String!, $authorid: ID!) {
		addSong(name: $name, genre: $genre, authorid: $authorid) {
			name,
			genre,
			authorid
		}
	}
`
export { getSongs, getSingers, addSong }
