import React, {useState} from 'react'
import {addingSinger} from '../queries/queries';
import {graphql} from 'react-apollo';
const AddSinger = props => {
	const [name, setName] = useState('');
	const [age, setAge] = useState('');
	function addSinger(e) {
		e.preventDefault();
		console.log(name, age);
	}
	return(
		<form onSubmit={e => addSinger(e)} id="addsinger" className="w3-padding w3-round w3-border w3-border-white w3-margin">
			<label>
				Tên ca sĩ: {' '}
				<input type='text' required onChange={e => setName(e.target.value)}/>
			</label><br/>
			<label>
				Tên bài hát: {' '}
				<input type='number' required onChange={e => setAge(e.target.value)}/>
			</label>
			<button>+</button>
		</form>
	)
}

export default graphql(addingSinger)(AddSinger)
