import React from 'react';
import { graphql } from 'react-apollo';
import { getSong } from '../queries/queries'
const Details = props => {
	function displayBook() {
		const {book} = props.data;
		console.log(book.name)
		if(book) {
			return(
				<div>
					<p>{book.name}</p>
				</div>
			)
		} 
	}
	return(
		<div>
			{displayBook()}
		</div>
	)
}

export default graphql(getSong, {
	options: (props) => {
		return {
			variables: {
				id: props.id
			}
		}
	}
}
)(Details)
