import React from 'react';
import { graphql } from 'react-apollo';
import { getSong } from '../queries/queries'
const Details = props => {
	const data = props.data.song || [];
	return(
		<div>
			<p>{data.name}</p>
			<p>{data.genre}</p>
			<ul>
				{data.singers.map(singer => <li key={singer.id}>{singer.name}</li>)}
			</ul>
		</div>
	)
}

export default graphql(getSong, {
	options: (props) => {
		return {
			variables: {
				id: props.id
			}
		}
	}
}
)(Details)
